export const Types = {
    columns : 'COLUMNS',
    board: 'BOARD',
    cards: 'CARDS',
};

export function chooseType (item) {
    switch (item.type) {
        case Types.board:
            return deleteBoardFunc(item.id);
        case Types.columns :
            return deleteColumnFunc(item.id);
        default:
            return deleteCardFunc({idCard: item.idCard, idColumn: item.idColumn});
        break;
    }
}
export default Types;
