import { createSlice } from '@reduxjs/toolkit';

export const boardSlice = createSlice({
  name: 'boards',
  initialState: {
    list: [],
    currentBoard: 0,
    idBoard: -1,
    idColumn: -1,
  },
  reducers: {
    addBoard: (state, action) => {
      state.idBoard += 1;
      state.list.push({ id: state.idBoard ,title: action.payload, columns: [] });
    },
    addColumn: (state, action) => {
      state.idColumn += 1;
      state.list[state.currentBoard].columns.push({id: state.idColumn, title: action.payload});
    },
    changeBoard: (state, action) => {
      state.currentBoard = action.payload;
    },
    deleteBoard: (state, action) => {
      const index = state.list.indexOf(state.list.find((element)=> element.id === action.payload));
      console.log(index)     
      state.list.slice(0,index);
    },
  },
})

// Action creators are generated for each case reducer function
export const { addBoard, addColumn, changeBoard , deleteBoard} = boardSlice.actions

export default boardSlice.reducer
