import React, {useState, useRef, useCallback} from "react";
import { useSelector, useDispatch } from "react-redux";
import { addColumn, deleteBoard } from "../reducers/boardSlice";
import Column from "../components/Column";
import { useDrop } from "react-dnd";
import "./Board.css"

export default function Board() {
  const columns = useSelector((state) => state.boards.list[state.boards.currentBoard].columns);
  const dispatch = useDispatch();
  const [title, setTitle] = useState('');
  const myRef = useRef(null);

  const [{isOver}, drop] = useDrop( () => ({
    accept: "board",
    drop: (item) => deleteBoardFunc(item.id),
    collect: (monitor)=>({
      isOver: !!monitor.isOver(),
  })
  }))

  const [{isOverColumn}, dropColumn] = useDrop( () => ({
    accept: "column",
    drop: (item) => deleteColumnFunc(item.id),
    collect: (monitor)=>({
      isOverColumn: !!monitor.isOver(),
  })
  }))

  useCallback (function() {
    drop.current = myRef.current;
    dropColumn.current = myRef.current;
  }, [drop, dropColumn, myRef]);

  const deleteBoardFunc = (id) => {dispatch(deleteBoard(id))};
  const deleteColumnFunc = (id) => {console.log(id)};

  return (
    <>
    <div className="component-board">
      {columns.map((column) => (
        <Column key={column.id} title={column.title}/>
      ))}
      <div className="component-column button-add">
        <input className="field-input" type="text" placeholder="Enter column title" onChange={event => setTitle(event.target.value)} value={title}/>
        <button
            className="create-button"
            onClick={() => {
                dispatch(addColumn(title));
                setTitle('');
            }}
            disabled={title===''}
        >
            Create Column
        </button>
      </div>
    </div>
    <button className="button-delete" ref={myRef}>Delete</button>
    </>
  );
}
