import React from "react";
import { useDispatch } from "react-redux";
import { changeCardTitle, moveCard } from "../reducers/boardSlice";
import { useDrag, useDrop } from "react-dnd";
import "./Card.css";

export default function Card(props) {
  const dispatch = useDispatch();

  const [, drag] = useDrag(() => ({
    type: "card",
    item: { idColumn: props.column.id, idCard: props.element.id, type: "CARD" },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [ {isOver} , drop] = useDrop( () => ({
    accept: ["board", "column", "card"],
    drop: (item) => {
      item.type === 'CARD' && moveCardFunc({idCard: item.idCard, idColumn: item.idColumn})
    },
    collect: (monitor)=>({
      isOver: !!monitor.isOver(),
  })
  }))

  const moveCardFunc = ({idCard,idColumn}) => {dispatch(moveCard({idCard: idCard,idColumn: idColumn, newIdColumn: props.column.id}))};

  return (
    <div className="card-box" ref={drag}>
      <input
        className="card-input"
        value={props.element.title}
        onChange={(event) =>
          dispatch(
            changeCardTitle({
              columnId: props.column.id,
              cardId: props.element.id,
              newTitle: event.target.value,
            })
          )
        }
      />
      <div className="box-end" ref={drop} />
    </div>
  );
}
