import React from "react";
import { useDispatch } from "react-redux";
import { addCard, changeColumnTitle, moveCard } from "../reducers/boardSlice";
import { useDrag, useDrop } from "react-dnd";
import Card from "./Card";
import "./Column.css"

export default function Column (props) {
    const dispatch = useDispatch();

    const [, drag ] = useDrag(()=>({
        type: "column",
        item: {id: props.column.id, type: 'COLUMN'},
        collect: (monitor)=>({
            isDragging: !!monitor.isDragging(),
        })
    }))

    const [ {isOver} , drop] = useDrop( () => ({
        accept: ["board", "column", "card"],
        drop: (item) => {
          item.type === 'CARD' && moveCardFunc({idCard: item.idCard, idColumn: item.idColumn})
        },
        collect: (monitor)=>({
          isOver: !!monitor.isOver(),
      })
      }))

      const moveCardFunc = ({idCard,idColumn}) => {dispatch(moveCard({idCard: idCard,idColumn: idColumn, newIdColumn: props.column.id}))};

    return (
      <div className="component-column" key={props.column.id} ref={{drag, drop}}>
        <div className="text-row">
          <input
            className="column-text"
            value={props.column.title}
            onChange={(event) =>
              dispatch(
                changeColumnTitle({
                  id: props.column.id,
                  newTitle: event.target.value,
                })
              )
            }
          />
          <button
            className="plus-button"
            onClick={() => {
              dispatch(addCard(props.column.id));
            }}
          >
            +
          </button>
        </div>
        {props.column.cards.map((element) => (
          <div key={element.id}>
            <Card element={element} column={props.column} />
          </div>
        ))}
      </div>
    );

}
